
DROP TABLE IF EXISTS tenders;

CREATE TABLE tenders (
    dept_railway_unit VARCHAR(255),
    tender_no VARCHAR(50) PRIMARY KEY,
    tender_title TEXT,
    status VARCHAR(50),
    work_area VARCHAR(100),
    due_date_time TIMESTAMP,
    due_days INTEGER,
    expiry_date DATE,
    vendor_lot_number VARCHAR(100),
    date_of_supply DATE,
    warranty_period INTEGER, -- in days, months, or years, depending on your requirement
    inspection_date DATE
);
